// seedDatabase.js - CORRECTED VERSION
const mongoose = require('mongoose');
const Paragraph = require('../models/Paragraph');

const seedDatabase = async () => {
  try {
    // Connect to MongoDB
    await mongoose.connect('mongodb://localhost:27017/signupdb', {
      useNewUrlParser: true,
      useUnifiedTopology: true
    });
    
    console.log('ЁЯЧСя╕П Clearing old data...');
    await Paragraph.deleteMany({});
    
    const paragraphs = [];
    
    // Define exact word counts - these match the "passage" field enum
    const wordCounts = [200, 300, 500, 1000];
    
    // Create 5 mocks
    const totalMocks = 5;
    
    console.log('ЁЯУЭ Generating paragraphs with exact word counts...\n');
    
    for (let mock = 1; mock <= totalMocks; mock++) {
      for (let passage of wordCounts) { // passage = word count number
        console.log(`ЁЯФи Creating Mock ${mock} - ${passage} words...`);
        
        // Generate EXACT word count paragraphs
        const hindiText = generateRealExamHindiParagraph(passage, mock);
        const englishText = generateRealExamEnglishParagraph(passage, mock);
        
        // Verify word counts
        const hindiWords = hindiText.trim().split(/\s+/).length;
        const englishWords = englishText.trim().split(/\s+/).length;
        
        if (hindiWords !== passage) {
          console.warn(`   тЪая╕П  Hindi: Expected ${passage}, got ${hindiWords}`);
        }
        if (englishWords !== passage) {
          console.warn(`   тЪая╕П  English: Expected ${passage}, got ${englishWords}`);
        }
        
        // HINDI PARAGRAPH
        paragraphs.push({
          language: 'hindi',              // Required field
          pattern: 'SSC',                 // Optional field with default
          time: '5 Min',                  // Required field - must match enum
          passage: passage,               // Required field - word count as NUMBER
          mock: mock,                     // Required field
          text: hindiText,                // Required field - actual paragraph text
          createdAt: new Date()           // Optional field with default
        });
        
        // ENGLISH PARAGRAPH
        paragraphs.push({
          language: 'english',            // Required field
          pattern: 'SSC',                 // Optional field with default
          time: '5 Min',                  // Required field - must match enum
          passage: passage,               // Required field - word count as NUMBER
          mock: mock,                     // Required field
          text: englishText,              // Required field - actual paragraph text
          createdAt: new Date()           // Optional field with default
        });
      }
    }
    
    console.log(`\nтЬЕ Generated ${paragraphs.length} paragraphs`);
    console.log('ЁЯТ╛ Saving to database...');
    
    await Paragraph.insertMany(paragraphs);
    
    console.log('тЬЕ Database seeded successfully!\n');
    
    // VERIFICATION REPORT
    console.log('ЁЯУК ========== VERIFICATION REPORT ==========');
    let allCorrect = true;
    
    for (let mock = 1; mock <= totalMocks; mock++) {
      console.log(`\nЁЯУЛ MOCK ${mock}:`);
      for (let passage of wordCounts) {
        const hindiDoc = await Paragraph.findOne({ language: 'hindi', mock: mock, passage: passage });
        const englishDoc = await Paragraph.findOne({ language: 'english', mock: mock, passage: passage });
        
        if (hindiDoc) {
          const actualWords = hindiDoc.text.trim().split(/\s+/).length;
          const expectedWords = hindiDoc.passage;
          const isCorrect = actualWords === expectedWords;
          if (!isCorrect) allCorrect = false;
          console.log(`   Hindi ${expectedWords} words: ${isCorrect ? 'тЬЕ' : 'тЭМ'} (${actualWords} actual)`);
        }
        
        if (englishDoc) {
          const actualWords = englishDoc.text.trim().split(/\s+/).length;
          const expectedWords = englishDoc.passage;
          const isCorrect = actualWords === expectedWords;
          if (!isCorrect) allCorrect = false;
          console.log(`   English ${expectedWords} words: ${isCorrect ? 'тЬЕ' : 'тЭМ'} (${actualWords} actual)`);
        }
      }
    }
    
    // Summary
    const totalDocs = await Paragraph.countDocuments();
    const hindiCount = await Paragraph.countDocuments({ language: 'hindi' });
    const englishCount = await Paragraph.countDocuments({ language: 'english' });
    
    console.log('\nЁЯУИ ========== SUMMARY ==========');
    console.log(`Total paragraphs: ${totalDocs}`);
    console.log(`Hindi paragraphs: ${hindiCount}`);
    console.log(`English paragraphs: ${englishCount}`);
    console.log(`All word counts correct: ${allCorrect ? 'тЬЕ YES' : 'тЭМ NO'}`);
    
    // Show samples
    console.log('\nЁЯСА ========== SAMPLE PARAGRAPHS ==========');
    console.log('\nHindi Sample (Mock 1, 200 words):');
    const hindiSample = await Paragraph.findOne({ language: 'hindi', mock: 1, passage: 200 });
    if (hindiSample) {
      console.log(`Document structure:`);
      console.log(`- Language: ${hindiSample.language}`);
      console.log(`- Passage (word count): ${hindiSample.passage}`);
      console.log(`- Mock: ${hindiSample.mock}`);
      console.log(`- Time: ${hindiSample.time}`);
      console.log(`- Pattern: ${hindiSample.pattern}`);
      console.log(`Text preview: ${hindiSample.text.substring(0, 150)}...`);
    }
    
    console.log('\nEnglish Sample (Mock 1, 200 words):');
    const englishSample = await Paragraph.findOne({ language: 'english', mock: 1, passage: 200 });
    if (englishSample) {
      console.log(`Document structure:`);
      console.log(`- Language: ${englishSample.language}`);
      console.log(`- Passage (word count): ${englishSample.passage}`);
      console.log(`- Mock: ${englishSample.mock}`);
      console.log(`- Time: ${englishSample.time}`);
      console.log(`- Pattern: ${englishSample.pattern}`);
      console.log(`Text preview: ${englishSample.text.substring(0, 150)}...`);
    }
    
    mongoose.connection.close();
    console.log('\nЁЯОЙ Database seeding complete!');
    
  } catch (error) {
    console.error('тЭМ Error:', error);
    
    // Show validation errors in detail
    if (error.errors) {
      console.log('\nЁЯФН VALIDATION ERRORS:');
      Object.keys(error.errors).forEach(field => {
        const err = error.errors[field];
        console.log(`  ${field}: ${err.message}`);
        console.log(`    Value: ${JSON.stringify(err.value)}`);
        console.log(`    Path: ${err.path}`);
      });
    }
    
    process.exit(1);
  }
};

